--lfte8 api--
local _chars = "ABCDEFGHIJKLMNOPQRSTUVWXYZ0123456789 \n#-_abcdefghijklmnopqrstuvwxyz@.()[]{}<>/\\+=&%$©|•," .. '"' .. "'?~:;	"
local out = {}
out.name = "LFTE8"
out.chars = {}
for i=1, #_chars do
	out.chars[_chars:sub(i, i)] = i
	out.chars[i] = _chars:sub(i, i)
end
out.parse = function(self, tb)
	local ot = ""
	local a = tb
	for i=1, #a do
		ot = ot .. self.chars[tonumber(a[i])]
	end
	return ot
end
out.deparse2tb = function(self, s)
	str = tostring(s)
	local tb = {}
	for i=1, #str do
		tb[i] = self.chars[str:sub(i, i)]
	end
	--print(tb, #tb)
	return tb
end
out.parsestr = function(self, str)
	return self:parse(str:split(' '))
end
class.opentycoon.api:api('lfte8', out)

--fileapi--

local otsav = {
	unpack = function(dat)
		local sf = {}
		local data = dat:split("\n")
		do
			sf.isuni = tonumber(data[1])
			sf.username = data[2]
			sf.length = #data - 2
			for i=1, sf.length do
				--print(i, i+2, sf.length)
				sf['line' .. i] = string.split(data[i+2], " ")
			end
		end
		return sf
	end,
	pack = function(sf)
		local data = sf.isuni .. '\n' .. sf.username
		for i=1, sf.length do
			data = data .. "\n"
			for vi=1, #sf['line' .. i] do
				data = data .. sf['line' .. i][vi] .. ' '
			end
			data = data:sub(1, #data - 1)
		end
		return data
	end,
	decypher = function(sf, k)
		local s = sf
		local key = tonumber('1' .. k)
		for i=1, sf.length do
			--print(i, sf['line' .. i])
			for vi=1, #sf['line' .. i] do
				s['line' .. i][vi] = sf['line' .. i][vi] - key
			end
		end
		return s
	end,
	cypher = function(sf, k)
		local s = sf
		local key = tonumber('1' .. k)
		for i=1, sf.length do
			for vi=1, #sf['line' .. i] do
				s['line' .. i][vi] = sf['line' .. i][vi] + key
			end
		end
		return s
	end
}
fileapi = {
	domain = 'opentycoon',
	type='fileapi',
	otsav = otsav
}
function fileapi:fileapi(name, tb)
	self[name] = tb
end
class:append(fileapi)